import {
    AppBar,
    Toolbar,
    Typography,
    makeStyles,
    Button,
    InputLabel,
    MenuItem,
    ListSubheader,
    FormControl,
    Select,
    IconButton,
    MenuIcon,
    Menu,
} from "@material-ui/core";
import { TreeSelect } from 'antd';
import React from "react";
import { Link as RouterLink } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
    header: {
        backgroundColor: "#400CCC",
        paddingRight: "79px",
        paddingLeft: "118px",
    },
    logo: {
        fontFamily: "Work Sans, sans-serif",
        fontWeight: 600,
        color: "#FFFEFE",
        textAlign: "left",
    },
    menuButton: {
        fontFamily: "Open Sans, sans-serif",
        fontWeight: 700,
        size: "18px",
        justifyContent: 'center',
        marginLeft: "38px",
    },
    toolbar: {
        display: "flex",
        flex: 1,
        justifyContent: "space-between",
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
        backgroundColor: '#e3e3e3',
        borderRadius: 5
    },
    text: {
        color: "primary",
    },
    rowHeader: {
        display: "flex",
    },
}));

const headersData = [
    {
        label: "Inbox",
        href: "/Inbox",
    },
    {
        label: "System Settings",
        href: "/System",
    },
];

const { TreeNode } = TreeSelect;


export default function Header() {

    const { header, logo, menuButton, toolbar, formControl, text, rowHeader } = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [value, setValue] = React.useState(undefined);

    const open = Boolean(anchorEl);

    const onChange = () => {
        setValue(value);
    };

    const handleMenu = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const displayDesktop = () => {
        return (
            <Toolbar className={toolbar}>
                {femmecubatorLogo}
                <div className={rowHeader}>
                    {getMenuButtons()}

                    <div>
                        <IconButton
                            aria-label="account of current user"
                            aria-controls="menu-appbar"
                            aria-haspopup="true"
                            onClick={handleMenu}
                            color="inherit"
                        >
                            <Button
                                {...{
                                    key: "User Settings",
                                    color: "inherit",
                                    to: "/account",
                                    component: RouterLink,
                                    className: menuButton
                                }}
                            >
                                {"User Settings"}
                            </Button>
                            {/* <AccountCircle /> */}
                        </IconButton>
                        <Menu
                            id="menu-appbar"
                            anchorEl={anchorEl}
                            anchorOrigin={{
                                vertical: 'top',
                                horizontal: 'right',
                            }}
                            keepMounted
                            transformOrigin={{
                                vertical: 'top',
                                horizontal: 'right',
                            }}
                            open={open}
                            onClose={handleClose}
                        >
                            <MenuItem onClick={handleClose}>Change Password</MenuItem>
                            <MenuItem onClick={handleClose}>User photo </MenuItem>
                            <MenuItem onClick={handleClose}>User name </MenuItem>
                            <MenuItem onClick={handleClose}>Last login : 6 hours ago </MenuItem>
                        </Menu>
                    </div>
                    <div>
                        <FormControl className={[formControl, menuButton]}>
                            <InputLabel className={text} htmlFor="grouped-native-select">Change Location</InputLabel>
                            <Select native defaultValue="" id="grouped-native-select">
                                <option aria-label="None" value="" />
                                <optgroup className={text} label="Khartoum">
                                    <option className={text} value={1}>Bahri</option>
                                    <option className={text} value={2}>Omdurman</option>
                                </optgroup>
                                <optgroup className={text} label="Gazira">
                                    <option className={text} value={3}>Madani</option>
                                    <option className={text} value={4}>Hasahisa</option>
                                </optgroup>
                            </Select>
                        </FormControl>

                    </div>
                </div>
            </Toolbar>
        );
    };

    const getMenuButtons = () => {
        return (
            <>
                {headersData.map(({ label, href }) => {
                    return (
                        <Button
                            onClick={() => { window.open("." + href); }}
                            {...{
                                key: label,
                                color: "inherit",
                                to: href,

                                component: RouterLink,
                                className: menuButton
                            }}
                        >
                            {label}
                        </Button>
                    );
                })}
            </>
        )
    };

    const femmecubatorLogo = (
        <Typography variant="h6" component="h1" className={logo}>
            SiddigHope
        </Typography>
    );

    return (
        <header>
            <AppBar className={header}>{displayDesktop()}</AppBar>
        </header>
    );
}
