import {
    AppBar,
    Toolbar,
    Typography,
    makeStyles,
    Button,
} from "@material-ui/core";
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import ListSubheader from '@material-ui/core/ListSubheader';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import React from "react";
import { Link as RouterLink } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
    header: {
        backgroundColor: "#400CCC",
        paddingRight: "79px",
        paddingLeft: "118px",
    },
    logo: {
        fontFamily: "Work Sans, sans-serif",
        fontWeight: 600,
        color: "#FFFEFE",
        textAlign: "left",
    },
    menuButton: {
        fontFamily: "Open Sans, sans-serif",
        fontWeight: 700,
        size: "18px",
        justifyContent: 'center',
        marginLeft: "38px",
    },
    toolbar: {
        display: "flex",
        flex: 1,
        justifyContent: "space-between",
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
        backgroundColor:'#e3e3e3',
        borderRadius: 10
    },
    text:{
        color: "primary",
    },
}));

const headersData = [
    {
        label: "Change Location",
        href: "/listings",
    },
    {
        label: "Inbox",
        href: "/inbox",
    },
    {
        label: "User settings",
        href: "/account",
    },
    {
        label: "System Settings",
        href: "/system",
    },
];

export default function Header() {

    const { header, logo, menuButton, toolbar, formControl, text } = useStyles();


    const displayDesktop = () => {
        return (
            <Toolbar className={toolbar}>
                {femmecubatorLogo}
                <div>{getMenuButtons()}</div>
                <FormControl className={[formControl, menuButton]}>
                    <InputLabel className={text} htmlFor="grouped-native-select">Change Location</InputLabel>
                    <Select native defaultValue="" id="grouped-native-select">
                        <option aria-label="None" value="" />
                        <optgroup className={text} label="Khartoum">
                            <option className={text} value={1}>Bahri</option>
                            <option className={text} value={2}>Omdurman</option>
                        </optgroup>
                        <optgroup className={text} label="Gazira">
                            <option className={text} value={3}>Madani</option>
                            <option className={text} value={4}>Hasahisa</option>
                        </optgroup>
                    </Select>
                </FormControl>
            </Toolbar>
        );
    };

    const getMenuButtons = () => {
        return (
            <>
                {headersData.map(({ label, href }) => {
                    return (
                        <Button
                            {...{
                                key: label,
                                color: "inherit",
                                to: href,
                                component: RouterLink,
                                className: menuButton
                            }}
                        >
                            {label}
                        </Button>
                    );
                })}
            </>
        )
    };

    const femmecubatorLogo = (
        <Typography variant="h6" component="h1" className={logo}>
            SiddigHope
        </Typography>
    );

    return (
        <header>
            <AppBar className={header}>{displayDesktop()}</AppBar>
        </header>
    );
}
