import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Form, Input, Button, Radio } from 'antd';
import { makeStyles } from '@material-ui/core';

const useStyle = makeStyles(() => ({
    container:{
        // height: '100vh', /* Magic here */
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor:'red'
    },
    form:{
        width:'50%',
        backgroundColor:'green'
    }
}))

const Account = () => {
    const [form] = Form.useForm();
    const classes = useStyle()
    const [formLayout] = useState('horizontal');


    const formItemLayout =
        formLayout === 'horizontal'
            ? {
                labelCol: {
                    span: 4,
                },
                wrapperCol: {
                    span: 14,
                },
            }
            : null;

    return (
        <div className={classes.container}>
            <Form
                className={classes.form}
                {...formItemLayout}
                layout={formLayout}
                form={form}
                initialValues={{
                    layout: formLayout,
                }}
            >
                <Form.Item>
                    <Input placeholder="SiddigHope" />
                </Form.Item>
                <Form.Item>
                    <Input type='password' placeholder="Type your old password here" />
                </Form.Item>
                <Form.Item>
                    <Input type='password' placeholder="Type your New password here" />
                </Form.Item>
                <Form.Item>
                    <Input type='password' placeholder="Type your New password again" />
                </Form.Item>
                <Form.Item >
                    <Button type="primary">Update Account</Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default Account
