import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Form, Input, Button, Radio } from 'antd';
import { makeStyles, Typography } from '@material-ui/core';

const useStyle = makeStyles(() => ({
    container:{
        // height: '100vh', /* Magic here */
        display: 'flex',
        justifyContent: 'space-around',
        flexDirection:'row',
        alignItems: 'center',
        backgroundColor:'red',
        marginRight:20,
        marginLeft:20,
    },
    form:{
        width:'50%',
        backgroundColor:'green',
    },
    data: {
        flex:1,
        // height: '100vh', /* Magic here */
        width:'100%',
        height: '100%',
        flexDirection:'column',
        justifyContent:'flex-end',
        backgroundColor:'yellow',
    }
}))

const Account = () => {
    const [form] = Form.useForm();
    const classes = useStyle()
    const [formLayout] = useState('horizontal');
    const [username, setUsername] = useState('SiddigHope');
    const [password, setPassword] = useState('password');
    const [photo, setPhoto] = useState('horizontal');


    const formItemLayout =
        formLayout === 'horizontal'
            ? {
                labelCol: {
                    span: 4,
                },
                wrapperCol: {
                    span: 14,
                },
            }
            : null;

    return (
        <div className={classes.container}>
            <Form
                className={classes.form}
                {...formItemLayout}
                layout={formLayout}
                form={form}
                initialValues={{
                    layout: formLayout,
                }}
            >
                <Form.Item>
                    <Input placeholder="SiddigHope" />
                </Form.Item>
                <Form.Item>
                    <Input type='password' placeholder="Type your old password here" />
                </Form.Item>
                <Form.Item>
                    <Input type='password' placeholder="Type your New password here" />
                </Form.Item>
                <Form.Item>
                    <Input type='password' placeholder="Type your New password again" />
                </Form.Item>
                <Form.Item >
                    <Button type="primary">Update Account</Button>
                </Form.Item>
            </Form>
            <div className={classes.data}>
                <Typography>
                    {username}
                </Typography>
                <Typography>
                    {password}
                </Typography>
                <Typography>
                    {photo}
                </Typography>
                <Typography>
                    {}
                </Typography>
            </div>
        </div>
    );
};

export default Account
