import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Form, Input, Button, Radio } from 'antd';
import { FormControl } from '@material-ui/core';


const Account = () => {
    const [form] = Form.useForm();
    const [formLayout, setFormLayout] = useState('horizontal');

    const onFormLayoutChange = ({ layout }) => {
        setFormLayout(layout);
    };

    const formItemLayout =
        formLayout === 'horizontal'
            ? {
                labelCol: {
                    span: 4,
                },
                wrapperCol: {
                    span: 14,
                },
            }
            : null;
    const buttonItemLayout =
        formLayout === 'horizontal'
            ? {
                wrapperCol: {
                    span: 14,
                    offset: 4,
                },
            }
            : null;
    return (
        <>
            <Form
                {...formItemLayout}
                layout={formLayout}
                form={form}
                initialValues={{
                    layout: formLayout,
                }}
            >
                <Form.Item label="User name ">
                    <Input placeholder="SiddigHope" />
                </Form.Item>
                <Form.Item label="Old Password">
                    <Input type='password' placeholder="Type your old password here" />
                </Form.Item>
                <Form.Item label="New Password">
                    <Input type='password' placeholder="Type your New password here" />
                </Form.Item>
                <Form.Item label="Confirm New Password">
                    <Input type='password' placeholder="Type your New password again" />
                </Form.Item>
                <Form.Item {...buttonItemLayout}>
                    <Button type="primary">Update Account</Button>
                </Form.Item>
            </Form>
        </>
    );
};

export default Account
